const products = [
    { id: "1", name: '1 Kilo', price: 15000, category: 'Helado', img: 'data:image/jpeg;base64,', stock: 50 },
    { id: '2', name: '3/4 Kilo', price: 12000, category: 'Helado', img: 'data:image/jpeg;base64,', stock: 50 },
    { id: '3', name: '1/2 Kilo', price: 8000, category: 'Helado', img: 'data:image/jpeg;base64,', stock: 50 },
    { id: '4', name: '1/4 Kilo', price: 4000, category: 'Helado', img: 'data:image/jpeg;base64,', stock: 50 },
    { id: '5', name: '2.5 Kilo', price: 30000, category: 'Helado', img: 'data:image/jpeg;base64,', stock: 50 },
    { id: '6', }



]

export const getProducts = () => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve(products)
        }, 500)
    })
}

export const getProductsById = (productId) => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve(products.find(prod => prod.id === productId))
        }, 500)
    })
}

export const getProductsByCategory = (productCategory) => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve(products.find(prod => prod.category === productCategory))
        }, 500)
    })
}